import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function Products() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true); // Loading state
  const [error, setError] = useState(null); // Error state
  const [searchQuery, setSearchQuery] = useState(""); // Search query state
  const [newProduct, setNewProduct] = useState({
    ProductName: "",
    Price: "",
  }); // New product state
  const [message, setMessage] = useState(""); // Message state for feedback
  const [showForm, setShowForm] = useState(false); // State to toggle the form visibility
  const token = localStorage.getItem("token");
  const navigate = useNavigate();

  useEffect(() => {
    if (!token) {
      // Redirect to login if token is not found
      navigate("/login");
      return;
    }

    axios
      .get("http://localhost:5000/api/products", { headers: { Authorization: `Bearer ${token}` } })
      .then((res) => {
        console.log(res.data); // Check the data structure
        setProducts(res.data.products || []); // If the data is inside "products", access it properly
        setLoading(false);
      })
      .catch((err) => {
        setLoading(false);
        setError(`Error fetching products: ${err.message}`);
      });
  }, [token, navigate]);

  const handleSearch = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleInputChange = (e) => {
    setNewProduct({ ...newProduct, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const { ProductName, Price } = newProduct;

    if (!ProductName || !Price) {
      setMessage("Please fill in all fields.");
      return;
    }

    const productData = {
      ProductName,
      Price: parseFloat(Price), // Ensure price is a number
    };

    axios
      .post("http://localhost:5000/api/products", productData, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => {
        setMessage("Product added successfully!");
        setNewProduct({ ProductName: "", Price: "" }); // Reset the form fields
        setProducts((prevProducts) => [...prevProducts, res.data.product]); // Add new product to the list
      })
      .catch((err) => {
        setMessage(`Error adding product: ${err.message}`);
      });
  };

  const filteredProducts = products.filter((product) =>
    product.ProductID.toString().includes(searchQuery)
  );

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center vh-100">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    ); // Show loading spinner
  }

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">Products</h2>

      {error && <div className="alert alert-danger text-center">{error}</div>} {/* Show error message if any */}
      {message && <div className="alert alert-info text-center">{message}</div>} {/* Show success/error message */}

      {/* Search Box */}
      <div className="mb-4">
        <input
          type="text"
          className="form-control"
          placeholder="Search by Product ID"
          value={searchQuery}
          onChange={handleSearch}
        />
      </div>

      {/* Add Product Form */}
      {showForm && (
        <div className="mb-4">
          <h3>Add New Product</h3>
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="ProductName" className="form-label">
                Product Name
              </label>
              <input
                type="text"
                className="form-control"
                id="ProductName"
                name="ProductName"
                value={newProduct.ProductName}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="Price" className="form-label">
                Price
              </label>
              <input
                type="number"
                className="form-control"
                id="Price"
                name="Price"
                value={newProduct.Price}
                onChange={handleInputChange}
                required
              />
            </div>
            <button type="submit" className="btn btn-success">
              Add Product
            </button>
          </form>
        </div>
      )}

      {/* Product List */}
      <div className="row">
        {filteredProducts.length > 0 ? (
          filteredProducts.map((p) => (
            <div className="col-md-4 mb-4" key={p.ProductID}>
              <div className="card shadow-sm">
                <div className="card-body">
                  <h5 className="card-title">{p.ProductName}</h5>
                  <p className="card-text">Price: ${p.Price}</p>
                  <a href="#" className="btn btn-primary">
                    View Details
                  </a>
                </div>
              </div>
            </div>
          ))
        ) : (
          <div className="col-12">
            <div className="alert alert-warning text-center">No products found</div>
          </div>
        )}
      </div>

      {/* Button at the bottom right */}
      <button
        className="btn btn-primary position-fixed bottom-0 end-0 m-7"
        onClick={() => setShowForm(!showForm)} // Toggle form visibility
      >
        {showForm ? "Hide Form" : "Add Product"}
      </button>
    </div>
  );
}

export default Products;
