import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";

function Order() {
  const location = useLocation();
  const { cart, total } = location.state || {};  // Access the data passed via navigate
  const [orderNumber] = useState(`ORD${Math.floor(Math.random() * 1000000)}`);

  if (!cart || cart.length === 0) {
    return <div className="alert alert-danger">No order data found!</div>;
  }

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">ðŸŽ‰ Order Confirmed</h2>

      <div className="text-center">
        <h4>Order Number: {orderNumber}</h4>
        <h5>Total Amount: ${total}</h5>
        <div className="mt-4">
          <h6>Your Order:</h6>
          <ul className="list-group">
            {cart.map((item, index) => (
              <li className="list-group-item" key={index}>
                {item.ProductName} - ${item.Price}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Order;
