import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

function Cart() {
  const [cart, setCart] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(storedCart);
  }, []);

  const handleRemoveFromCart = (index) => {
    const updatedCart = [...cart];
    updatedCart.splice(index, 1);
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">üõí Shopping Cart</h2>

      {cart.length === 0 ? (
        <div className="alert alert-warning text-center">Your cart is empty.</div>
      ) : (
        <div className="row">
          {cart.map((item, index) => (
            <div className="col-md-4 mb-4" key={index}>
              <div className="card shadow-sm">
                <div className="card-body">
                  <h5 className="card-title">{item.ProductName}</h5>
                  <p className="card-text">Price: ${item.Price}</p>
                  <button
                    className="btn btn-danger"
                    onClick={() => handleRemoveFromCart(index)}
                  >
                    Remove
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

        <div className="text-center mt-4">
            <button className="btn btn-secondary" onClick={() => navigate("/products")}>
                ‚¨ÖÔ∏è Continue Shopping
            </button>
        </div>
        <div className="text-center mt-4">
        <button className="btn btn-success mt-3 w-100" onClick={() => navigate("/payment")}>
                Proceed to Payment
        </button>
        </div>
    </div>
  );
}

export default Cart;
