import React, { useEffect, useState } from "react";

function Order() {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    // Retrieve orders from localStorage
    const savedOrders = JSON.parse(localStorage.getItem("orders")) || [];
    setOrders(savedOrders);
  }, []);

  if (orders.length === 0) {
    return <div className="alert alert-danger text-center mt-5">No orders found!</div>;
  }

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">ðŸ“¦ Your Orders</h2>

      {orders.map((order, index) => (
        <div key={index} className="mb-5">
          <h4>Order Number: {order.orderId}</h4>
          <h5>Total Amount: ${order.total}</h5>
          <p>Order Date: {new Date(order.timestamp).toLocaleString()}</p>
          <div className="row">
            {order.cart.map((item, idx) => (
              <div className="col-md-4 mb-4" key={idx}>
                <div className="card shadow-sm">
                  <div className="card-body">
                    <h5 className="card-title">{item.ProductName}</h5>
                    <p className="card-text">Price: ${item.Price}</p>
                    <p className="card-text">Quantity: 1</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
          <hr />
        </div>
      ))}
    </div>
  );
}

export default Order;
