import React, { useState, useEffect } from "react";

function Order() {
  const [orderDetails, setOrderDetails] = useState(null);

  useEffect(() => {
    // You can retrieve order details from localStorage or pass them via route state
    const orderData = JSON.parse(localStorage.getItem("orderDetails"));
    setOrderDetails(orderData);
  }, []);

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">ðŸ“¦ Order Summary</h2>

      {orderDetails ? (
        <div className="shadow p-4 bg-light rounded">
          <h4>Order Number: {orderDetails.orderNumber}</h4>
          <p><strong>Product:</strong> {orderDetails.productName}</p>
          <p><strong>Price:</strong> ${orderDetails.price}</p>
          <p><strong>Status:</strong> {orderDetails.status}</p>
        </div>
      ) : (
        <div className="alert alert-warning text-center">No order details available.</div>
      )}
    </div>
  );
}

export default Order;
