const handlePayment = (e) => {
    e.preventDefault();
  
    if (!cardNumber || !expiryDate || !cvv || !name) {
      setMessage("Please fill in all fields.");
      return;
    }
  
    if (!/^\d{16}$/.test(cardNumber)) {
      setMessage("Invalid card number. Must be 16 digits.");
      return;
    }
  
    if (!/^\d{3,4}$/.test(cvv)) {
      setMessage("Invalid CVV. Must be 3 or 4 digits.");
      return;
    }
  
    setMessage("Payment successful! Redirecting...");
    setTimeout(() => {
      const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
      const orderTotal = storedCart.reduce((sum, item) => sum + item.Price, 0);
  
      localStorage.removeItem("cart");
  
      // Pass cart data and total to the Order page
      navigate("/orders", { state: { cart: storedCart, total: orderTotal } });
    }, 2000);
  };
