import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

function Order() {
  const [cart, setCart] = useState([]);
  const [total, setTotal] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(storedCart);
    setTotal(storedCart.reduce((acc, item) => acc + item.Price, 0));
  }, []);

  const handleConfirmOrder = () => {
    // Here you would typically send the order data to your backend
    // For now, we are just redirecting to the order tracking page
    navigate("/order-tracking");
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">Review Your Order</h2>

      {cart.length === 0 ? (
        <div className="alert alert-warning text-center">Your cart is empty.</div>
      ) : (
        <div className="row">
          {cart.map((item, index) => (
            <div className="col-md-4 mb-4" key={index}>
              <div className="card shadow-sm">
                <div className="card-body">
                  <h5 className="card-title">{item.ProductName}</h5>
                  <p className="card-text">Price: ${item.Price}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      <div className="text-center mt-4">
        <h4>Total: ${total}</h4>
      </div>

      <div className="text-center mt-4">
        <button className="btn btn-secondary" onClick={() => navigate("/cart")}>
          ⬅️ Go Back to Cart
        </button>
      </div>

      <div className="text-center mt-4">
        <button className="btn btn-success" onClick={handleConfirmOrder}>
          Confirm Order
        </button>
      </div>
    </div>
  );
}

export default Order;
