import React from "react";
import { useLocation } from "react-router-dom";

function Order() {
  const location = useLocation();
  const { cart, total } = location.state || {}; // Retrieve the cart and total from navigation state

  if (!cart || cart.length === 0) {
    return <div className="alert alert-danger">No order data found!</div>;
  }

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">ðŸŽ‰ Order Confirmed</h2>

      <div className="text-center">
        <h4>Order Summary</h4>
        <h5>Total Amount: ${total}</h5>
        <div className="mt-4">
          <h6>Your Order:</h6>
          <div className="row">
            {cart.map((item, index) => (
              <div className="col-md-4 mb-4" key={index}>
                <div className="card shadow-sm">
                  <div className="card-body">
                    <h5 className="card-title">{item.ProductName}</h5>
                    <p className="card-text">Price: ${item.Price}</p>
                    <p className="card-text">Quantity: 1</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Order;
