import React, { useEffect, useState } from "react";

function Order() {
  const [order, setOrder] = useState(null);

  useEffect(() => {
    // Retrieve order data from localStorage
    const savedOrder = JSON.parse(localStorage.getItem("order"));
    if (savedOrder) {
      setOrder(savedOrder);
    }
  }, []);

  if (!order) {
    return <div className="alert alert-danger text-center mt-5">No order data found!</div>;
  }

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">ðŸŽ‰ Order Confirmed</h2>

      <div className="text-center">
        <h4>Order Number: {order.orderNumber}</h4>
        <h5>Total Amount: ${order.total}</h5>
        <p>Order Date: {new Date(order.timestamp).toLocaleString()}</p>
        
        <div className="row mt-4">
          {order.cart.map((item, index) => (
            <div className="col-md-4 mb-4" key={index}>
              <div className="card shadow-sm">
                <div className="card-body">
                  <h5 className="card-title">{item.ProductName}</h5>
                  <p className="card-text">Price: ${item.Price}</p>
                  <p className="card-text">Quantity: 1</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Order;
