import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

function Payment() {
  const [cart, setCart] = useState([]);
  const [paymentMethod, setPaymentMethod] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(storedCart);
  }, []);

  const getTotalPrice = () => {
    return cart.reduce((total, item) => total + item.Price, 0);
  };

  const handlePayment = () => {
    if (!paymentMethod) {
      setMessage("Please select a payment method.");
      return;
    }

    // Simulate payment processing
    setMessage(`Payment via ${paymentMethod} Successful! Redirecting...`);
    setTimeout(() => {
      localStorage.removeItem("cart"); // Clear cart after payment
      navigate("/"); // Redirect to homepage
    }, 2000);
  };

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">üí≥ Select Payment Method</h2>

      {message && <div className="alert alert-success text-center">{message}</div>}

      <div className="card p-4 shadow-sm">
        <h5>Total Amount: ${getTotalPrice().toFixed(2)}</h5>

        <div className="mt-3">
          <label className="form-label">Choose Payment Method:</label>
          <select
            className="form-select"
            value={paymentMethod}
            onChange={(e) => setPaymentMethod(e.target.value)}
          >
            <option value="">Select...</option>
            <option value="Credit Card">Credit Card</option>
            <option value="PayPal">PayPal</option>
            <option value="Bank Transfer">Bank Transfer</option>
            <option value="Cash on Delivery">Cash on Delivery</option>
          </select>
        </div>

        <button className="btn btn-primary mt-3" onClick={handlePayment}>
          Proceed to Payment
        </button>
      </div>

      <div className="text-center mt-4">
        <button className="btn btn-secondary" onClick={() => navigate("/cart")}>
          ‚¨ÖÔ∏è Back to Cart
        </button>
      </div>
    </div>
  );
}

export default Payment;
