import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function Orders() {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const token = localStorage.getItem("token");
  const navigate = useNavigate();

  useEffect(() => {
    if (!token) {
      navigate("/login");
      return;
    }
  
    // Assuming the user ID is part of the token (JWT) payload, extract it
    const userId = JSON.parse(atob(token.split('.')[1])).userId; // Example: Decoding JWT to get userId
  
    // Fetch orders for the specific user
    axios
      .get(`http://localhost:5000/api/orders/${userId}`, { // Using dynamic URL
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => {
        setOrders(Array.isArray(res.data) ? res.data : []);
        setLoading(false);
      })
      .catch((err) => {
        setError("Failed to load orders. Please try again later.");
        setLoading(false);
      });
  }, [token, navigate]);
  

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center vh-100">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return <div className="alert alert-danger text-center">{error}</div>;
  }

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">🛒 Your Orders</h2>
      {orders.length > 0 ? (
        <div className="table-responsive">
          <table className="table table-bordered table-hover text-center">
            <thead className="table-dark">
              <tr>
                <th>#</th>
                <th>Order ID</th>
                <th>Order Date</th>
                <th>Total Price</th>
                <th>Status</th>
                <th>Created At</th>
                <th>Updated At</th>
              </tr>
            </thead>
            <tbody>
              {orders.map((o, index) => (
                <tr key={o.OrderID || index}>
                  <td>{index + 1}</td>
                  <td>{o.OrderID}</td>
                  <td>{new Date(o.OrderDate).toLocaleDateString()}</td>
                  <td>{`$${o.TotalPrice.toFixed(2)}`}</td>
                  <td>{o.Status}</td>
                  <td>{new Date(o.CreatedAt).toLocaleDateString()}</td>
                  <td>{new Date(o.UpdatedAt).toLocaleDateString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <div className="text-center text-muted">No orders available</div>
      )}
    </div>
  );
}

export default Orders;
